const compile = require('./compile')
module.exports = Object.assign (
    cds_parse, compile, {help: `
# SYNOPSIS

    *cds parse* ...

    Convenient shortcut for 'cds compile --flavor parsed'.

# OPTIONS

    - see _cds help compile_


`})

function cds_parse (projects, options) {
    return compile (projects,{ flavor:'parsed', ...options })
}
