/* eslint-disable no-unused-vars */
/**
 * @abstract
 */
module.exports = class BuildTaskProvider {
    /**
     * @param {object} task 
     */
    providesTask(key) { }
    /**
     * @returns {object} task 
     */
    getTask(key) { return key }
    /**
     * @param {object} task 
     */
    getPlugin(task) { }
    /**
     * @param {Array} tasks 
     * @param {boolean} dependencies 
     */
    async lookupTasks(tasks, dependencies) { }
    /**
     * @param {object} task 
     */
    async applyTaskDefaults(task) { }
}
