"use strict"

const { importOpenAPI } = require("./importOpenAPI");
let messages = require("../message").getMessages();

function openAPI2csn(source) {
    let csn = {};
    try {
        const fileContentToJSON = JSON.parse(source);
        csn = importOpenAPI(fileContentToJSON);
    } catch (e) {
        throw new Error(messages.INVALID_OPENAPI_FILE);
    }
    return csn;
}

module.exports = {
    openAPI2csn
}