const mvn = require('../../mvn')
const { readProject } = require('../../projectReader')
const { H2 } = require('../../constants').OPTIONS

module.exports = class H2Template extends require('../../plugin') {

  async canRun() {
    const { isNodejs } = readProject()
    if (isNodejs) throw `'cds add ${H2}' is not available for Node.js`
    return true
  }

  async run() {
    await mvn.add(H2)
  }
}
