const path = require('path');
const fs = require('fs').promises;

const { concatUrls } = require('./urls');

const MTX_URL_PATH_PREFIX = 'mtx/v1/model/';

module.exports = {
    async checkProject(folder) {
        return await fs.access(path.join(folder, 'package.json')).then(() => true).catch(() => false);
    },
    getMtxApiUrl(url, action) {
        return concatUrls(url, MTX_URL_PATH_PREFIX, action);
    }
}
