const { FILES, GLOBALS, PLUGIN_NAME } = require('../constants')
const { parserPath } = require('../api')

function _createConfig (config) {
  return {
    root: true,
    globals: GLOBALS,
    plugins: [PLUGIN_NAME],
    overrides: [
      {
        files: FILES,
        parser: parserPath
      }
    ],
    rules: config
  }
}

module.exports = {
  all: _createConfig(require('./all')),
  recommended: _createConfig(require('./recommended'))
}
